#!/usr/bin/python# -*- coding: UTF_8 -*-import randomclass allIn():    """This is temporarily something which will make a player randomly leave a table."""    def __init__(self, wantToJoinAGame=True, wantToLeaveTheTable=False):        self.name = "allIn"        self.wantToJoin = wantToJoinAGame        self.wantToLeave = wantToLeaveTheTable    def tossACoin(self):        return bool(random.getrandbits(1))    def wantToJoinAGame(self):        return self.tossACoin()    def wantToLeaveAGame(self):        return self.tossACoin()if __name__ == "__main__":    pass